import Link from 'next/link'
// npm install --save isomorphic-unfetch
import fetch from 'isomorphic-unfetch'

function EdbContent(props) {
    console.warn("data", props.data.data)
    return (
        <>
            <h1>Fetch data from API</h1>
            {
                props.data.data.map(data => (
                    <div key={data.attributes.drupal_internal__nid}>
                        <Link as={data.attributes.path.alias} href={`/post?title=${data.id}`}>
                            <a>{data.attributes.title}</a>
                        </Link>
                        <div>{data.attributes.body.summary}</div>
                    </div>
                ))
            }

        </>
    )
}


EdbContent.getInitialProps = async function () {
    //const res = await fetch('https://jsonplaceholder.typicode.com/todos')
    const res = await fetch('http://uxteam:neo2020@redesign.docksal/jsonapi/node/tutorials_?page[limit]=10&filter[status][value]=1')
    const data = await res.json();
    return {
        data
    }
}

export default EdbContent
