// npm install --save isomorphic-unfetch
import fetch from 'isomorphic-unfetch'

function EdbContent(props) {
    console.warn("data", props)
    return (
        <>
            <h1>Fetch data from API</h1>

            {
                props.data.map(data => (
                    <div>
                        <Link as={`/posts/${data.id}`} href={`/post?title=${data.title}`}>
                            <a>{data.title}</a>
                        </Link>
                    </div>
                ))
            }
        </>
    )
}

