import Link from 'next/link'
// npm install --save isomorphic-unfetch
import fetch from 'isomorphic-unfetch'

function EdbContent(props) {
    console.warn("data", props.data.data)
    return (
        <>
            <h1>Fetch data from API</h1>
            {
                props.data.data.map(data => (
                    <div>
                        <Link as={`/posts/edb-content/${data.dataid}`} href={`/post?title=${data.title}`}>
                            <a>{data.title}</a>
                        </Link>
                    </div>
                ))
            }

        </>
    )
}

